INSERT INTO `similar_src` VALUES ('1', '火币Pro', 'https://www.huobi.pro/', 'huobi.pro', '火币全球专业站，是火币集团旗下服务于全球专业交易用户的创新数字资产交易平台，致力于发现优质的创新数字资产投资机会，目前提供四十多种数字资产品类的交易及投资服务，总部位于新加坡，由火币全球专业站团队负责运营。火币集团是一家具有全球竞争力与影响力的数字资产综合服务商，为超过130个国家百万级用户提供优质服务。在新加坡、香港、韩国、日本等多个国家和地区均有独立的交易业务和运营中心。在技术平台、产品支线、安全风控体系、运营及客户服务体系等方面，火币集团在全球均处于领先地位。', '2018-02-23 16:40:43', '中国', '08630bd44a7dacd1a037af2377a4df4f');
