INSERT INTO `similar_src` VALUES ('118', 'Hypex', 'https://www.hypex.io', 'hypex.io', 'Hypex是一家位于澳洲的交易所，是以红烧肉为基准货币的币对币交易所，目前仅支持HSR、AE与BTC的交易。\n总部位于澳大利亚，为全球首个以Hcash作为基准货币的币对币交易平台，主要是向全球用户提供Hcash的现货与衍生币种交易服务，但同时也支持Hcash对主流币种的交易。Hypex网站支持中文界面。\n', '2018-02-23 16:40:45', '澳洲', '8d90cb3a3b73735ccac27ea803dbafc2');
