INSERT INTO `similar_src` VALUES ('128', '666DAX', 'http://666dax.com/', '666dax.com', '由新加坡dax公司于2016年10月创办，公司注册资本1500万新元，2016年11月正式上线。上线之初即获得天使资金投资，投资的60%用于平台推广和宣传,其核心团队由多个关注虚拟货币多年的技术人员组成.', '2018-02-23 16:40:46', '新加坡', '3a05dc1bace3a2bd3e7ff9aad541571f');
