INSERT INTO `similar_src` VALUES ('155', 'Exmo', 'https://exmo.com/', 'exmo.com', 'XMO公司汇集了来自全球各地的专业家。自来西班牙、俄罗斯、印度和泰国的程序员同自来英国、美国、立陶宛和新加坡的成功的金融顾问员一起创建世界上最方便的数字货币交易平台。专家指出比特币能使金融计算变得更简单，更安全和更好并EXMO为区块链技术发展作出自己的贡献。', '2018-02-23 16:40:46', '西班牙', 'e80b837db4a7ff4d0b077f7484cbf75c');
